(window.webpackJsonp=window.webpackJsonp||[]).push([[108],{492:function(v,_,t){"use strict";t.r(_);var s=t(54),r=Object(s.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h1",{attrs:{id:"企业文化建设规划"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化建设规划"}},[v._v("#")]),v._v(" 企业文化建设规划")]),v._v(" "),t("h2",{attrs:{id:"企业文化的内涵"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化的内涵"}},[v._v("#")]),v._v(" 企业文化的内涵")]),v._v(" "),t("p",[v._v("企业文化理论是大约在1984年传入我国，党的十四大明确提出，在精神文明建设方面要搞好企业文化建设。进入21世纪后，企业文化建设逐渐成为企业提高管理水平、提升核心竞争力的自觉行为。")]),v._v(" "),t("p",[v._v("从企业文化概念的引入到今天，很多优秀企业都有自己独特的文化，例如华为的“奋斗文化”，阿里的“快乐文化”，海底捞的“极致客户服务文化”等等。尽管我们可以指出企业文化的成功范例，但是还有很多人对企业文化存在很多认知误区，比如认为文化就是领导者文化，或者觉得企业文化就是标语和口号。那到底什么是企业文化呢？")]),v._v(" "),t("p",[v._v("企业文化是企业员工上下一致共同遵守的价值体系，一种员工都清楚的行为准则，由企业环境、价值观、榜样、习俗与仪式、文化传播网络五个要素组成。企业文化是企业发展过程中形成的独特的个性，包括信仰、观念、价值观、态度、工作方式、工作氛围及工作行为。")]),v._v(" "),t("p",[v._v("埃德加.沙因提出了文化三层次理论以及企业文化四层次结构。企业文化的三个层次是表层的物质文化、中间的制度文化、最底层的核心价值观。企业文化的四层次结构分别为：精神层、制度层、行为层、物质层。")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2022/10/20/65467aebfe93f3c7.png",alt:""}})]),v._v(" "),t("p",[v._v("下图是华为核心企业文化的构成，在理念层上，华为确定了“以客户为中心，以奋斗者为本”的核心价值观，并明确了奋斗文化的内涵。在制度上华为对奋斗者进行了良好的界定，华为将员工分为普通劳动者和奋斗者。在行为层上，华为制定了奋斗者行为规范和军规，通过划定红线对奋斗者行为进行约束。在物质层上，华为内部设置了许多奖项，通过激励来促使更多的人成为奋斗者。")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2022/10/20/0e3e17dd8ed0d042.png",alt:""}})]),v._v(" "),t("p",[v._v("总体而言，企业文化有导向、凝聚、约束、激励、纽带、辐射等功能，企业文化可以形成一种凝聚力，对企业员工起到“粘合剂”的作用。一个公司有着优秀的企业文化，就能凝聚员工、汇聚人才、提高团队作战能力。企业文化会随着公司的实际发展与其相适应，因此要依据企业基本经营理念制定发展战略，根据环境的发展匹配相应的文化。")]),v._v(" "),t("h2",{attrs:{id:"企业文化的调研与诊断"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化的调研与诊断"}},[v._v("#")]),v._v(" 企业文化的调研与诊断")]),v._v(" "),t("p",[v._v("为了弄清企业文化发展水平和发展状态，应当定期对企业文化进行调研和诊断，审视企业文化环境，检验企业价值观导向是否与企业发展环境、组织结构、管理制度、员工关系等情况相匹配。任何一家企业都要经历从无到有，从小到大的发展历程，都要经历创业、成长、成熟三大阶段。企业文化也是在企业发展过程中逐步孕育和发展起来的，并且相应的表现出不同的层次。")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("企业创业期")]),v._v("。此时的文化跟着创始人的思想走，企业人文化就是创始人文化，企业文化建设需要创始人发挥带头作用。创业期形成的文化形态会对企业后续发展有很大影响。")]),v._v(" "),t("li",[t("strong",[v._v("企业成长期")]),v._v("。此时管理者必须依靠制度和文化来管理企业，但是容易陷入“重宣传、轻执行”的误区，在这一阶段需要掌握企业文化建设的科学方法，把提炼出来的标语、口号、手册、流程制度配备齐全。")]),v._v(" "),t("li",[t("strong",[v._v("企业成熟期")]),v._v("。这一时期，企业规模和人员结构相对复杂，企业文化要获得广大员工的认同，就需要再既有文化上做出调整，更多的突出企业发展的新方向、新目标以及相匹配的文化元素，需要配套容易为员工认可和接受的价值理念。")])]),v._v(" "),t("p",[v._v("企业文化的诊断和建设效果评估，应贯穿于企业文化建设的始终，企业在不同的发展阶段，存在着不同的文化形态，因此，我们在企业文化提炼、导入、变革的各个阶段里，都需要进行企业文化诊断，其包括：企业文化与企业实际环境的匹配程度、企业文化落实情况、企业文化是否有助于企业竞争力提升等等。")]),v._v(" "),t("p",[v._v("国外有一些相对成熟的企业文化诊断工具，例如以参与性、一致性、适应性和使命为特征的组织文化模型，它将每一种文化特征又细分出了三个考察维度：")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2022/10/21/b0f84f0bd110de1b.png",alt:""}})]),v._v(" "),t("p",[v._v("可以借助组织文化模型，通过问卷调查的形式，让员工对企业文化的12个维度进行评分，并根据各个维度的得分情况了解企业文化全貌。适应性和使命强调的是企业对于外部环境的适应能力，参与性和一致性强调的是企业内部的和谐能力，两者共同反应了企业文化建设只能怪索要平衡和解决的主要冲突。")]),v._v(" "),t("h2",{attrs:{id:"企业文化建设目标规划"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化建设目标规划"}},[v._v("#")]),v._v(" 企业文化建设目标规划")]),v._v(" "),t("p",[v._v("在了解了企业文化调研和诊断工具之后，着手开展企业文化建设和落地工作之前，企业还需要做好企业文化建设和落地的目标规划，明确企业文化建设的阶段安排和具体行动计划。企业战略和企业文化是相互制约、相互影响的，企业要重点培养与战略相适应的企业文化，企业需要以企业战略为导向进行企业文化梳理，以促进企业战略目标的实现。麦肯锡指出，影响企业发展的因素包括战略、结构、制度、风格、人员、技能、共同价值观等7个方面。")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2022/10/22/66501fc14aba410d.png",alt:""}})]),v._v(" "),t("p",[v._v("根据传统的阶段划分方法，我们可以将企业文化建设过程依次分为三个阶段，分别是组织准备、全面宣导与落地，巩固、改善和提升。")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("组织准备阶段")]),v._v("：具体包括组件企业文化管理队伍，明确相关部门和人员的职责，形成分级管理体系，各负其责，齐抓共管，高效协同的长效机制。完善企业文化建设管理机制，通过完善制度体系、监督体系、激励体系、保障体系，来确保企业文化建设工作的高效开展。确定企业文化建设推进策略，提炼企业文化理念体系，设计企业文化大纲，制定员工手册，普及企业文化常识，让其意识到当下建设企业文化的可能性、必要性和紧迫性等。")]),v._v(" "),t("li",[t("strong",[v._v("全面宣导与落地阶段")]),v._v("：通过教育培训，做好企业核心理念的宣传推广工作，通过打造企业文化教育培训内容体系、组织多样化的企业文化教育培训活动，培养强有力的企业文化师资队伍，使企业文化得以全面推广。根据企业文化相关理念，选定需要修订的目标制度，制定相应的修订计划，建立起与企业文化配套的管理制度，使有些的企业文化理念得以固化。根据员工在企业文化宣贯过程中的反馈，制定出一套易于上员工接受和遵守的行为规范，通过领导者以身作则，树立优秀典范，借助标杆案例等示范传承方式，引领员工做出与企业文化导向一致的行为。通过示范班组建设、专项文化建设促进企业文化融入经营管理中。")]),v._v(" "),t("li",[t("strong",[v._v("巩固、改善与提升阶段")]),v._v("：这一阶段主要的任务是通过考评激励和多元传播，对初具规模的企业文化进行巩固、改善和提升，形成持续改进的良性闭环系统。这里，企业要在内部成员中建立起普遍的文化信仰，提升员工的跟随度和忠诚度，强化企业的凝聚力和战斗力。此外，企业还要在社会上建立起统一化、个性化的企业形象，持续提升企业知名度和商誉。")])]),v._v(" "),t("h2",{attrs:{id:"企业文化落地策略"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化落地策略"}},[v._v("#")]),v._v(" 企业文化落地策略")]),v._v(" "),t("p",[v._v("理解企业文化的内涵和功能、掌握企业文化调研和诊断的方法，最终都是为了推动企业文化的有效落地。当全体员工都理解、认可企业文化理念，并在工作中自觉的予以实践时，企业文化就落地了。")]),v._v(" "),t("p",[v._v("一个企业的文化必须是在经营管理过程中沉淀出来的，很多企业在建设企业文化时存在“文化理想”现象，将企业文化建设目标定位在企业可承受的范围之外，脱离企业实际，例如很多提出大而空的口号，把社会口号、政治口号照搬成了企业口号，导致企业文化建设空泛。也有人认为企业文化建设就是按照领导者的意图，自上而下的拖行，这就陷入了主客体关系认知的误区，企业文化建设必须是所有员工共同建设的过程，从群众中来、到群众中去。只有让企业领导者的战略思考、主导作用与广大员工的参与有机结合，才能让企业文化发挥其应有的生命力。企业文化建设应该将企业精神文化、制度文化、行为文化和物质文化加以融合。")]),v._v(" "),t("p",[v._v("企业文化的“内化于心、外化于行、固化预制、物化于境”是落地的目标，落地可以参照如下常见的方法：")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("价值观量化")]),v._v("。将每一条抽象的核心价值观细化为员工和管理人员的行为准则，让全体员工都深知哪些行为必为，哪些行为必不为。")]),v._v(" "),t("li",[t("strong",[v._v("激励差异化")]),v._v("。通过价值观保持方向正确，通过KPI和OKR指导做事正确，针对不同的员工表现和绩效，给与不同的激励，以促进员工工作的积极性。")]),v._v(" "),t("li",[t("strong",[v._v("案例故事化")]),v._v("。将创业艰辛、努力工作、安全生产、服务客户的优秀员工公司拍成视频或做成宣传报道，通过媒体传播来引领和激烈其他员工。")]),v._v(" "),t("li",[t("strong",[v._v("内容可视化")]),v._v("。将企业文化理念用可视化的形式呈现出来，并设置在洗手间、食堂、文化墙等任何员工都能看到和感知的场所。")]),v._v(" "),t("li",[t("strong",[v._v("文化道具化")]),v._v("。将企业文化以道具的形式陪伴员工，例如将工牌视为每一个员工独一无二的文化传承，借助徽章、挂饰等道具传播特色的企业文化。")]),v._v(" "),t("li",[t("strong",[v._v("仪式固定化")]),v._v("。针对企业文化可以设定对应的节日已经仪式，将企业价值观融入到仪式中。")])]),v._v(" "),t("p",[v._v("总体而言，企业文化要明确企业使命、愿景、核心价值观，确定发展路径、经营方略、组织建设与管理准则，制定领导干部和员工的行为准则，利用文化仪式、文化产品、文化故事、环境布置、典范引领等手段，营造企业文化常态化传播的氛围，塑造良好的企业形象。要通过建立系统的文化落地机制，形成员工与企业之间的心理默契，促使员工理解、认同企业文化并自觉践行。")]),v._v(" "),t("h2",{attrs:{id:"企业文化职责分工"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#企业文化职责分工"}},[v._v("#")]),v._v(" 企业文化职责分工")]),v._v(" "),t("p",[v._v("要确保企业文化建设的成果，仅仅掌握企业文化落地方法还远远不够，关键还要看执行，企业文化建设工作由谁领导和统筹管理，由谁负责具体推动，这是执行过程中需要解决的关键问题。企业必须建立“高层统一领导、各部门相互协调与配合，横到边、纵到底”的建设推进网络。")]),v._v(" "),t("p",[v._v("如果把企业文化建设比作一搜航线中的“船只”，那么企业文化建设的领导和统筹者就相当于船长，在企业文化建设工作中，要明确相关人的责任，使不同层级的人员主动承担好自己在企业文化中的使命。在企业文化建设实践过程中，不同层级的人员所担任的角色和负担的使命是不同的。")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("企业家")]),v._v("在指明企业发展方向外，身体力行的推行自己所倡导的文化理念，是企业家的职责。要建立好强有力的文化，企业家必须先端正自己的态度，履行好自己的职责，身体力行的倡导和推行自己提出的价值理念。")]),v._v(" "),t("li",[t("strong",[v._v("企业文化建设小组")]),v._v("，可以在企业家指明方向后，以项目化的形式推动企业文化建设工作，负责文化建设氛围的营造，审核公司企业文化建设规划、计划和实施意见、管理办法和工作流程。")]),v._v(" "),t("li",[t("strong",[v._v("协同执行人员")]),v._v("在企业文化建设领导小组的指挥下，根据建设实施的行动计划，将企业文化建设重点任务细化到本部门，确保每个事项有人管，有人落实。")])]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2022/10/24/f4501c3102c662f0.png",alt:""}})]),v._v(" "),t("p",[v._v("通过责任分解，调动全体人员参与到企业文化建设中，形成层层抓落实、全员协同的良好局面。为了提升企业文化建设工作中相关人员的履责效能，要做好两项工作。第一，梳理企业文化建设各阶段的工作任务，形成任务清单。第二，根据任务清单，明确相关部门和人员的岗位责任和工作责任，形成责任清单，逐项明确任务书、责任人和履责形式。")])])}),[],!1,null,null,null);_.default=r.exports}}]);