(window.webpackJsonp=window.webpackJsonp||[]).push([[116],{503:function(t,e,l){"use strict";l.r(e);var s=l(54),v=Object(s.a)({},(function(){var t=this,e=t.$createElement,l=t._self._c||e;return l("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[l("h1",{attrs:{id:"前言"}},[l("a",{staticClass:"header-anchor",attrs:{href:"#前言"}},[t._v("#")]),t._v(" 前言")]),t._v(" "),l("blockquote",[l("p",[t._v("韩陈昊")])]),t._v(" "),l("p",[t._v("20世纪90年代以来，文化的经济功能正在逐步被认同和推动，国内外学界对文化资源及其产业化开发的研究也逐渐深入。国外学术界对文化资源开发的研究主要集中在个别文化资源案例研究和具体类别文化资源在开发中的作用分析。国内历史文化资源开发研究主要是对著名的文化资源事项进行案例性研究，理论性的探讨都从文化资源开发的关系及对策方面着手，缺乏研究的系统性。")]),t._v(" "),l("p",[t._v("国内研究对文化资源的开发及两者相同的关系有较多的论述，而对我国文化资源产业化开发的战略研究比较薄弱。国内关于文化资源开发的研究更多聚焦于一些国家级和世界级的文化名城和文化遗产，而对于数量繁多、在我国文化资源中占据重要地位的地方性文化资源研究较少。通常学者都拘泥于开发或者拘泥于文化资源本身，无法将两者结合起来进行综合权衡考虑。")]),t._v(" "),l("p",[t._v("这种情况会产生一定的局限性，无法适应我国文化资源产业发展的迅猛需要。文化资源产业化开发的实践迫切需要更具现实性、系统性的理论研究成果出现。文化资源产业化战略研究的意义在于：")]),t._v(" "),l("ol",[l("li",[t._v("开展文化资源产业化战略研究，对于维护国家安全具有重要作用。中国加入WTO，面对经济全球化、世界市场一体化的格局，保持民族文化多样性，保护珍贵的民族文化资源，增强民族文化传统的现代适应性，对于维护国家和民族的文化形态和政治安全和文化资源经济安全有着重要的意义。")]),t._v(" "),l("li",[t._v("开展文化资源产业化开发战略研究，有利于发展先进文化生产力。伴随着文化产业化而来的是文化产品的商业化，文化产品生产者的智力成果通过有偿交换的方式得以出让。文化产品智力价值在文化产业条件下的实现，提高了文化生产者的积极性和创造性，也在更大程度上满足了人的文化需求，实现了文化自身的目的。")]),t._v(" "),l("li",[t._v("开展文化资源产业化开发战略研究，对于提高人们精神文化素质具有重要意义。在文化产业的作用下，文化产品从个别走向一般，从精英文化走向大众文化，从奢侈品变为普通消费品，这使大众能够更多的消费文化产品，从而在更大范围内提高人们的精神为文化素质。")])])])}),[],!1,null,null,null);e.default=v.exports}}]);