(window.webpackJsonp=window.webpackJsonp||[]).push([[258],{641:function(v,_,t){"use strict";t.r(_);var s=t(54),r=Object(s.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h1",{attrs:{id:"智慧城市顶层设计方法"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#智慧城市顶层设计方法"}},[v._v("#")]),v._v(" 智慧城市顶层设计方法")]),v._v(" "),t("p",[v._v("顶层设计源于工程领域的自上而下设计（top-down design）。顶层设计的本意是指开始的总体构想，是一种针对某一具体的设计对象，从目标系统的整体高度和全局观念出发，运用系统论的方式进行总体构想和战略设计，自上而下逐层分解、细化。")]),v._v(" "),t("p",[v._v("在开展顶层设计的工作的过程中，是对发展战略在时间、空间的展现形态和发展路线的整体设计，这不仅取决于技术，更取决于理念和人。顶层设计最关键的是要对系统的整体目标和全局观念有着准确的感知和把握。")]),v._v(" "),t("p",[v._v("一般而言，顶层设计应基于现状，开展体系需求分析、体系架构设计、体系方案验证等工作，提出建设目标、应用需求、能力要求、技术机制、实施路径等总体构想，以便系统的提升城市服务能力。顶层设计包含了多个层次的设计方法和过程，如总体规划设计、业务架构设计、详细步骤设计，在总体框架的约束和限制下，自上而下、逐步细化、层层控制，提高了系统设计的执行力，保障设计效果的可控和目标的稳步实现。")]),v._v(" "),t("p",[v._v("在进行顶层设计的时候，必须要考虑到设计结果广泛的普适性，设计时要兼顾城市发展的差异性，尽量弱化IT技术的主导地位，要重视城市的管理和运营，充分利用有限的资金投入来高效地开展智慧城市建设。在实操工作时，必须要抓住共性，约定统一的数据格式与接口，为以后的资源整合打好基础，为行业数据制定统一的数据格式和模版，为各信息化系统融合预留统一的接口。")]),v._v(" "),t("p",[v._v("随着城市化步伐的加快，大量乡村人口涌入城市，快速发展带来了交通拥挤、环境恶化、公共安全、舆论隐患等问题，针对这些问题，除了行政、市场等手段，技术解决方案也发挥着重要的作用，所以在做顶层设计时，相关规划需要围绕着城市政务、产业、交通、社区、民生、市政等各个方面的信息基础设施建设或者信息应用，强调云计算、物联网、大数据等新一代信息技术支撑下的城市智能管理，促进城市可持续发展，构建多规融合的城市协同规划体系。")]),v._v(" "),t("h2",{attrs:{id:"顶层设计方法论"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#顶层设计方法论"}},[v._v("#")]),v._v(" 顶层设计方法论")]),v._v(" "),t("p",[v._v("智慧城市顶层设计针对智慧城市建设，从全局出发进行架构设计，对整个架构的各个方面、各个层次、各种相关方、各种正面的促进因素和负面的限制因素进行统筹考虑和设计。")]),v._v(" "),t("p",[v._v("智慧城市顶层架构设计不仅需要技术应用于业务目标融合叠加，也需要以“"),t("strong",[v._v("重管理性、轻技术性、求系统性")]),v._v("”的新标准观为指导，通过战略类标准、流程类标准和技术类规范着力构建体系化设计能力、从信息到智慧的转化能力，以及社会治理多元化的转型能力。")]),v._v(" "),t("p",[v._v("智慧城市顶层设计首先要明确城市的目标定位，弄清楚为什么建的问题，这是整个智慧城市系统设计的灵魂。做好目标定位设计，要注意把握好三个“结合”：")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("与城市科学发展的主题相结合")]),v._v("。智慧城市系统应融合于整个城市大系统，支撑整个城市大系统高效运转，按照“政治、经济、文化、社会、生态”五位一体和新型工业化、信息化、城镇化和农业现代化同步发展要求，围绕政府、企业、居民、生态环境四个方面的主体功能优化，统筹城市发展的物质资源、信息资源和智力资源，聚集提升政府行政效能、促进经济转型升级、保障和改善民生、合理开发利用自然资源资源等城市科学发展四大主题，促进经济社会全面协调可持续发展。")]),v._v(" "),t("li",[t("strong",[v._v("与城市特色和优势相结合")]),v._v("。由于每个城市都有其特殊性，在具体目标定位设计中，应以城市发展战略需求为导向，基于城市的地理区位、历史文化、资源禀赋等特色，合理设定智慧城市建设目标，要注重在全面规划的基础上有所侧重，发挥好比较优势。")]),v._v(" "),t("li",[t("strong",[v._v("与城市信息化的基础条件相结合")]),v._v("。智慧城市是城市信息化的升级和发展，对信息基础设施、科技、人才、产业支撑以及居民信息素养等方面要求较高，因此，要在现有信息化的基础上，合理设定智慧城市的建设目标，防止脱离客观条件、目标过于理想或偏低，都会丧失新一轮城市创新发展的机遇，市区竞争优势。")])]),v._v(" "),t("p",[v._v("架构设计在于确定智慧城市建设的主要内容和任务，解决智慧城市“建什么”的问题，这个问题要围绕着城市目标定位，构建智慧城市组成的基本系统总体框架。还要结合城市特色和实际，有侧重的搞好每个领域的设计，注重发挥优势，弥补差距，优先设计一些技能解决城市当前问题，又能推动城市长远发展的重点应用项目。")]),v._v(" "),t("p",[v._v("智慧城市顶层设计的体系方法可以沿袭EA企业架构的模式，由业务架构、应用架构、数据架构和基础设施架构四个方面组成。")]),v._v(" "),t("ul",[t("li",[t("strong",[v._v("业务架构设计")]),v._v("：业务架构是采用有效的建模技术，规范化地描述业务发展战略、业务运作模式、各个业务之间相互作用的关系结构，它从战略任务触发，以业务活动和要素为主线，以完成任务的信息流为支撑，描述基本业务模型。业务架构最大的特点是描述业务时，以业务或服务为中心进行职能梳理，这样有利于厘清部门之间的合作关系，构建集成化的信息平台，减少重复投资，增加信息沟通的机会和能力，优化业务服务过程。只有建立起业务架构设计，才能在不冲破现有体制的前提下实现部门内部以及部门之间的协同。")]),v._v(" "),t("li",[t("strong",[v._v("应用架构设计")]),v._v("：应用架构统筹规划和整体布局智慧城市应用体系，全面展现面向市民、企业和政府机关提供服务的整体设计，是智慧城市建设取得成效的关键内容。智慧城市应用架构设计要综合考虑产业、民生和政务三大领域的核心需求，提炼出智慧城市建设和应用的基础支撑、平台应用、智能展现层的核心任务，提出应用系统部署的基本构想。")]),v._v(" "),t("li",[t("strong",[v._v("数据架构设计")]),v._v("：数据架构设计主要是建立数据模型、描述信息流程，并依据业务架构分析信息的集成、共享、高效利用方式，进而实现业务流程的优化和重组，提高信息集成和共享能力。必须以资源共享为基础，以信息资源交换体系和资源目录体系为指引，实现信息资源的共享与复用。信息架构是用于描述支持业务流程运行中信息与数据交换共享的数据标准，提供了信息标准化描述和组织的模型。")]),v._v(" "),t("li",[t("strong",[v._v("基础设施架构设计")]),v._v("：基础设施架构包含三个层次，其分别是可感知的基础设施层、三网融合的通信层、云计算平台。感知设备识别物体、采集信息，通过传输网络将信息汇聚到网关，其主要解决数据获取问题。三网融合是指利用交换软件将电信网、互联网、电视网在技术、业务和产业方面融合起来，将感知层采集到的书记传送到云计算数据中心作为数据资源入库。云计算可以提供“按需计算”服务，它能够进行水平扩展，纵向扩容。")])]),v._v(" "),t("h2",{attrs:{id:"标准评价体系"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#标准评价体系"}},[v._v("#")]),v._v(" 标准评价体系")]),v._v(" "),t("p",[v._v("智慧城市建设过程中必须要制定科学的标准规范和评价体系，其应涉及智慧城市建设各项领域，能够指导智慧城市建设，标准体系能够与国家标准及行业标准纵向贯通，同时做到与现有应用领域的横向衔接。下图是标准体系架构参考图：")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2023/07/23/be7444109ddc45b1.png",alt:""}})]),v._v(" "),t("p",[v._v("智慧城市评价体系是由一套评价指标构成，使建设成果能够进行量化计算、科学评测，因此指标评价体系将是引领、监测指导、量化评估智慧城市建设的重要工具：")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://s3.bmp.ovh/imgs/2023/07/23/31748312d1bb229d.png",alt:""}})])])}),[],!1,null,null,null);_.default=r.exports}}]);